import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Genres from './pages/Genres.js';
import './App.css';

const styles = `
  * { margin: 0; padding: 0; box-sizing: border-box; }
  body { 
  font-family: Times New Roman, 
  sans-serif; 
  background-color: #f5f5f5; 
  }
  .container 
  { max-width: 1200px; 
   margin: 0 auto; 
   background-color: white; 
   min-height: 100vh; 
   border: 2px solid #333; 
   }
  .header { 
  display: flex; 
  justify-content: space-between; 
  align-items: center; 
  border-bottom: 2px solid #333; 
  }
  .logo { 
  width: 60px; 
  height: 60px; 
  border: 2px solid #333; 
  border-radius: 50%; 
  display: flex; 
  align-items: center; 
  justify-content: center; 
  font-weight: bold; 
}
  .nav 
  { 
  display: flex; 
  gap: 40px; 
   }
  .nav a 
  { text-decoration: none; 
   color: #333; 
   font-size: 18px; 
   cursor: pointer; 
   padding-bottom: 10px; 
   padding-top: 0px
   }
  .nav a:hover, .nav a.active 
  { 
    background-color: #e4cabd;
    border-radius: 8px;
   }
  .search-container { 
  position: relative; 
  padding-right: 20px;

  }
  .search { 
  padding: 10px 20px; 
  border: 2px solid #333; 
  border-radius: 25px; 
  font-size: 16px; 
  width: 200px; 
  background-color: #e4cabd;
  }
  .content { 
  padding: 20px 0; 
  }
  .page { 
  display: none; 
  }
  .page.active { 
  display: block; 
  }
  .image-section { 
  text-align: center; 
  padding-bottom: 30px;
  margin-bottom: 20px; 
  background-color: #ebebeb;
  }
  .mission { 
  margin-top: 30px; 
  padding: 20px; 
  border-bottom: 2px solid #333; 
  }
  .mission h2 { 
  font-size: 24px; 
  margin-bottom: 15px; 
  text-decoration: underline; 
  }
  .footer-text { 
  position: absolute; 
  bottom: 20px; 
  left: 20px; 
  font-weight: bold; 
  }
  .grid-container { 
  display: grid; 
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); 
  gap: 20px; 
  margin-top: 20px; 
  }
  .book-item { 
  border: 1px solid #ddd; 
  padding: 15px; 
  text-align: center; 
  background-color: #ebebeb; 
  border-radius: 5px; 
  }
  .resource-item { 
  border: 1px solid #ddd; 
  padding: 20px; 
  margin-bottom: 15px; 
  background-color: #ebebeb; 
  border-radius: 5px; 
  }
`;

function App() {
  const [activePage, setActivePage] = useState('App');
  const [searchTerm, setSearchTerm] = useState('');

  const handleNavClick = (page) => {
    setActivePage(page);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
    // You can implement actual search/filter logic here
    console.log('Searching for:', e.target.value);
  };

  return (
    <Router>
      <div style={{ backgroundColor: "#e4cabd" }}>
      <style>{styles}</style>
      <header className="header">
        <img src="/images/logo.png" alt="Logo" />
        <main>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/genres" element={<Genres />} />
          <Route path="/libraries" element={<Libraries />} />
          <Route path="/resources" element={<Resources />} />
        </Routes>
      </main>
      <div className="search-container">
          <input
            type="text"
            className="search"
            placeholder="search"
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
      </header>
    </div>
    </Router>
  );
}

export default App;
